import "./Services.css";
import HeartEmoji from "../../img/heartemoji.png" 
import Glasses from "../../img/glasses.png"
import Humble from "../../img/humble.png";
import Card from "../Card/Card";
import {motion} from "framer-motion"
// import Resume from "./Resume.pdf"

export const Services = () => {
  const transition = {duartion:1, type:"spring"}
  return (
    <div className="services">
      {/* left side      */}
      <div className="awesome">
        <span>My Awsome</span>
        <span>services</span>
        <span>
          Lorem, ipsum et consectetur adipisicing elit.
          quam dolores,  
          <br/>
          Lorem ipsu ui at blanditiis distinctio.
        </span>
        {/* <a href={Resume} download> */}
        <button className="button s-button" >Download CV</button>
        {/* </a> */}
        <div className="blur s-blurl"  style={{backgrond:"#ABF1FF94"}}></div>
      </div>
      {/* rigtrh side */}
      <div className="cards">'
      
                  <motion.div
                    whileInView = {{left:"14rem"}}
                    initial={{left:"25%"}}
                    transition={transition}
                    style={{left:"20rerm"}}> 
                      <Card
                        emoji= {HeartEmoji}
                        heading={"Design"}
                        detail = {"Figma, sketch, Photoshop,  Adobe,  Adobe xd"}
                        />
                  </motion.div>

                  {/* SECON D CARD */}
      <motion.div 
      whileInView = {{left:"1rem"}}
      initial={{left:"0%"}}
      transition={transition}
      style={{top:"24rem", left:"8rem"}}>
        <Card 
        emoji={Glasses}
        heading={"Developer"}
        detail = {"Html, Css, Javscript, React"}
        />
      </motion.div>

      {/* THIRD CARD */}
      <motion.div 
      whstyle={{top:"19rem", left:"30rem"}}>
        <Card 
        emoji={Humble}
        heading={"UI/UX"}
        detail={"Lorem kashidh js jod o osajdiq dnm idsd sno nakjca jsdnfkd fdof w"}/>
      </motion.div>
      <div className="blur s-blur2" style={{background: "var(--purple)"}}>
      </div>
      </div>   
      </div>
  )
}
